mapper.select:
SELECT d.Title, d.Subject, d.Description, d.Identifier, d.Object_Type, d.Object_Size, d.Digital_Data_Type, d.File_Extension, d.Rights_Statement_Media, d.Providing_Institution, d.Data_Code FROM `data` AS d
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
/*AND Providing_Institution=${Providing_Institution}*/ AND Providing_Institution='National Fine Arts Museum'
AND Rights_Statement_Media=${Rights_Statement_Media}
AND Digital_Data_Type=${Digital_Data_Type}
AND File_Extension=${File_Extension}
;

SELECT d.Title, d.Subject, d.Description, d.Identifier, d.Object_Type, d.Object_Size, d.Digital_Data_Type, d.File_Extension, d.Rights_Statement_Media, d.Providing_Institution, d.Data_Code, tf.fileIdx, tf.filePath, tf.fileOriginalName FROM `data` AS d INNER JOIN `tb_file_control` AS tfc ON d.Data_Code = tfc.referenceCode INNER JOIN `tb_file` AS tf ON tfc.fileIdx = tf.fileIdx where 1=1
AND d.Providing_Institution in
#{providingInstitution}
AND d.Rights_Statement_Media in
#{rightsStatementMedia}
AND d.Object_Type in
#{objectType}
AND d.Digital_Data_Type in
#{digitalDateType}
AND d.File_Extension in
#{fileExtension}
;

SELECT d.Title_Ru, d.Subject_Ru, d.Description_Ru, d.Identifier, d.Object_Type_Ru, d.Object_Size_Ru, d.Digital_Data_Type_Ru, d.File_Extension, d.Rights_Statement_Media, d.Providing_Institution_Ru, d.Data_Code, d.Created_Time, tf.fileIdx, tf.filePath, tf.fileOriginalName FROM `data` AS d INNER JOIN `tb_file_control` AS tfc ON d.Data_Code = tfc.referenceCode INNER JOIN `tb_file` AS tf ON tfc.fileIdx = tf.fileIdx where 1=1
AND d.Providing_Institution_Ru in
#{providingInstitution}
AND d.Rights_Statement_Media in
#{rightsStatementMedia}
AND d.Object_Type_Ru in
#{objectType}
AND d.Digital_Data_Type_Ru in
#{digitalDateType}
AND d.File_Extension in
#{fileExtension}
;
